INSERT INTO `preguntas`(`id`,`titulo_id`,`texto`,`epigrafe`,`pagina_pdf`,`pagina_bop`,`explicacion`,`respuesta_correcta`) VALUES (2,1,'¿Cuándo son nulos los acuerdos del Pleno y qué excepción permite tratar asuntos no incluidos en el orden del día?','Título I. Del Pleno y sus Comisiones – Régimen de sesiones',33,8953,'El art. 49 declara la nulidad por tratar asuntos no incluidos salvo especial y previa declaración de urgencia con el quórum de mayoría simple.','Son nulos si se adoptan en sesiones extraordinarias sobre asuntos no incluidos; la excepción es la previa declaración de urgencia con mayoría simple.');
