INSERT INTO `preguntas`(`id`,`titulo_id`,`texto`,`epigrafe`,`pagina_pdf`,`pagina_bop`,`explicacion`,`respuesta_correcta`) VALUES (71,1,'Según el ROM, ¿qué elementos debe contener el acta de cada sesión plenaria y cuál es el sistema preferente de documentación?','Título I. Del Pleno y sus Comisiones – Sección 2ª. Actas',50,8970,'El art. 99 fija el contenido mínimo del acta y establece la vídeo‑acta como sistema preferente, acompañada de certificación de autenticidad e integridad.','Debe incluir asistentes, orden del día, lugar y tiempo, puntos principales de las deliberaciones y acuerdos; el sistema preferente es la vídeo‑acta.');
INSERT INTO `opciones`(`id`,`pregunta_id`,`texto`,`es_correcta`) VALUES (281,71,'Solo debe incluir el resultado de las votaciones; el sistema preferente es el audio‑acta sin acta escrita.',0);
INSERT INTO `opciones`(`id`,`pregunta_id`,`texto`,`es_correcta`) VALUES (282,71,'Debe incluir preguntas del público; el sistema preferente es acta manuscrita firmada por todos los concejales.',0);
