-- MariaDB schema for Test Web App with PDF-BOP equivalence
SET NAMES utf8mb4;
START TRANSACTION;
SET FOREIGN_KEY_CHECKS=0;
DROP TABLE IF EXISTS `respuestas_usuario`;
DROP TABLE IF EXISTS `tests`;
DROP TABLE IF EXISTS `usuarios`;
DROP TABLE IF EXISTS `opciones`;
DROP TABLE IF EXISTS `preguntas`;
DROP TABLE IF EXISTS `titulos`;
DROP TABLE IF EXISTS `temas`;

CREATE TABLE `temas` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `nombre` VARCHAR(255) NOT NULL,
  `descripcion` TEXT,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE `titulos` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `tema_id` INT NOT NULL,
  `nombre` VARCHAR(255) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_tema` (`tema_id`),
  CONSTRAINT `fk_titulo_tema` FOREIGN KEY (`tema_id`) REFERENCES `temas`(`id`) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE `preguntas` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `titulo_id` INT NOT NULL,
  `texto` TEXT NOT NULL,
  `epigrafe` VARCHAR(255),
  `pagina_pdf` INT,
  `pagina_bop` INT,
  `explicacion` TEXT,
  `respuesta_correcta` TEXT,
  PRIMARY KEY (`id`),
  CONSTRAINT `fk_pregunta_titulo` FOREIGN KEY (`titulo_id`) REFERENCES `titulos`(`id`) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE `opciones` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `pregunta_id` INT NOT NULL,
  `texto` TEXT NOT NULL,
  `es_correcta` TINYINT(1) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`),
  CONSTRAINT `fk_opcion_pregunta` FOREIGN KEY (`pregunta_id`) REFERENCES `preguntas`(`id`) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE `usuarios` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `nombre` VARCHAR(100) NOT NULL,
  `email` VARCHAR(150) NOT NULL UNIQUE,
  `password_hash` VARCHAR(255) NOT NULL,
  `fecha_registro` DATETIME DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE `tests` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `usuario_id` INT NOT NULL,
  `tema_id` INT NOT NULL,
  `titulo_id` INT,
  `fecha_realizacion` DATETIME DEFAULT CURRENT_TIMESTAMP,
  `puntuacion` DECIMAL(5,2),
  `aprobado` TINYINT(1) DEFAULT 0,
  `num_preguntas` INT,
  `num_correctas` INT,
  PRIMARY KEY (`id`),
  CONSTRAINT `fk_test_usuario` FOREIGN KEY (`usuario_id`) REFERENCES `usuarios`(`id`) ON UPDATE CASCADE ON DELETE CASCADE,
  CONSTRAINT `fk_test_tema` FOREIGN KEY (`tema_id`) REFERENCES `temas`(`id`) ON UPDATE CASCADE ON DELETE CASCADE,
  CONSTRAINT `fk_test_titulo` FOREIGN KEY (`titulo_id`) REFERENCES `titulos`(`id`) ON UPDATE CASCADE ON DELETE SET NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE `respuestas_usuario` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `test_id` INT NOT NULL,
  `pregunta_id` INT NOT NULL,
  `opcion_id` INT NOT NULL,
  `es_correcta` TINYINT(1) NOT NULL,
  PRIMARY KEY (`id`),
  CONSTRAINT `fk_resp_test` FOREIGN KEY (`test_id`) REFERENCES `tests`(`id`) ON UPDATE CASCADE ON DELETE CASCADE,
  CONSTRAINT `fk_resp_pregunta` FOREIGN KEY (`pregunta_id`) REFERENCES `preguntas`(`id`) ON UPDATE CASCADE ON DELETE CASCADE,
  CONSTRAINT `fk_resp_opcion` FOREIGN KEY (`opcion_id`) REFERENCES `opciones`(`id`) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

SET FOREIGN_KEY_CHECKS=1;
COMMIT;